mapper.select:
SELECT uf.id, uf.user_id, ru.user_name as userName, ru.wechat, ru.phone_number as phoneNumber,
uf.feedback_content, uf.feedback_img AS feedbackImgStr, uf.feedback_time,
uf.feedback_count, uf.status
FROM rb_user_feedback uf
LEFT JOIN rb_user ru ON uf.user_id = ru.id


        AND uf.user_id = #{dto.userId}


        AND ru.user_name LIKE CONCAT('%', #{dto.userName}, '%')


        AND ru.wechat LIKE CONCAT('%', #{dto.wechat}, '%')


        AND ru.phone_number LIKE CONCAT('%', #{dto.phoneNumber}, '%')


        AND uf.feedback_time >= #{dto.minFeedbackTime}


        AND uf.feedback_time <= #{dto.maxFeedbackTime}


        AND uf.status = #{dto.status}


ORDER BY uf.feedback_time DESC
