mapper.select:
select id, title, content_form, send_time, send_type, status
from rb_system_message


        AND send_time BETWEEN #{dto.startTime}


        AND #{dto.endTime}


        AND title LIKE CONCAT('%', #{dto.title}, '%')


ORDER BY send_time DESC

select id, title, content_form, content, cover, send_time, send_type, send_user
from rb_system_message
where id = #{id}
